import {useState} from 'react';

function Todo(props) {

  const [modalIsOpen, setModelIsOpen] = useState(false);

  function deleteHandler(){
    setModelIsOpen(true);
  }

  return (
    <div className="card">
      <h2>{props.text}</h2>
      <div className="actions">
        <span>Some description </span>
        <button className="btn" onClick={deleteHandler} >Delete</button>
      </div>
      <Modal />
      <Backdrop />
    </div>
  );
}

export default Todo;
